.custom-cursor {
  position: fixed;
  left: 50%;
  top: 50%;
  width: 36px;
  height: 36px;
  pointer-events: none;
  border-radius: 50%;
  z-index: 9999;
  display: block;
  transition: transform 160ms cubic-bezier(.2,.8,.2,1), opacity 180ms ease, border-color 180ms ease;
  -webkit-transform: translate(-50%, -50%);
}

.cursor-outer {
  border: 2px solid rgba(16, 24, 40, 0.65);
  background: rgba(255, 255, 255, 0.02);
  box-shadow: 0 2px 10px rgba(2, 6, 23, 0.06);
}

.cursor__dot {
  position: fixed;
  left: 50%;
  top: 50%;
  width: 8px;
  height: 8px;
  background: rgba(16, 24, 40, 0.9);
  border-radius: 50%;
  transform: translate(-50%, -50%);
  transition: transform 140ms cubic-bezier(.2,.8,.2,1), background 140ms ease, width 140ms ease, height 140ms ease;
  z-index: 10000;
}

/* interactive state when hovering links/buttons */
.cursor--interactive {
  transform: translate(-50%, -50%) scale(1.6);
  border-color: var(--accent, #06b6d4);
}

.cursor__dot--interactive {
  transform: translate(-50%, -50%) scale(1.3);
  background: var(--accent, #06b6d4);
}

/* hide if reduced motion requested */
@media (prefers-reduced-motion: reduce) {
  .custom-cursor,
  .cursor__dot {
    transition: none !important;
  }
}

/* small-screen: reduce size */
@media (max-width: 640px) {
  .custom-cursor { display: none; }
}
