import React, { useEffect, useRef, useState } from "react";
import "./CustomCursor.css";

export default function CustomCursor() {
  const outerRef = useRef(null);
  const innerRef = useRef(null);
  const rafRef = useRef(null);
  const mouse = useRef({ x: -9999, y: -9999 });
  const pos = useRef({ x: window?.innerWidth / 2 || 0, y: window?.innerHeight / 2 || 0 });
  const [isInteractive, setIsInteractive] = useState(false);

  useEffect(() => {
    if (typeof window === "undefined") return;

    // Hide on touch devices (mobile)
    if ("ontouchstart" in window) {
      if (outerRef.current) outerRef.current.style.display = "none";
      return;
    }

    const prefersReduced = window.matchMedia && window.matchMedia("(prefers-reduced-motion: reduce)").matches;

    function onMove(e) {
      mouse.current.x = e.clientX;
      mouse.current.y = e.clientY;

      const t = e.target;
      const interactive = t && t.closest && !!t.closest("a,button,input,textarea,select,summary,[role=button],[data-cursor-interactive]");
      setIsInteractive(interactive);

      if (prefersReduced) {
        // follow instantly when reduced motion is requested
        pos.current.x = mouse.current.x;
        pos.current.y = mouse.current.y;
        if (outerRef.current) {
          outerRef.current.style.left = `${pos.current.x}px`;
          outerRef.current.style.top = `${pos.current.y}px`;
        }
        if (innerRef.current) {
          innerRef.current.style.left = `${pos.current.x}px`;
          innerRef.current.style.top = `${pos.current.y}px`;
        }
      }
    }

    function loop() {
      // smooth the movement
      pos.current.x += (mouse.current.x - pos.current.x) * 0.15;
      pos.current.y += (mouse.current.y - pos.current.y) * 0.15;

      if (outerRef.current) {
        outerRef.current.style.left = `${pos.current.x}px`;
        outerRef.current.style.top = `${pos.current.y}px`;
      }
      if (innerRef.current) {
        innerRef.current.style.left = `${pos.current.x}px`;
        innerRef.current.style.top = `${pos.current.y}px`;
      }

      rafRef.current = requestAnimationFrame(loop);
    }

    window.addEventListener("mousemove", onMove);
    rafRef.current = requestAnimationFrame(loop);

    return () => {
      window.removeEventListener("mousemove", onMove);
      if (rafRef.current) cancelAnimationFrame(rafRef.current);
    };
  }, []);

  useEffect(() => {
    if (!outerRef.current || !innerRef.current) return;
    outerRef.current.classList.toggle("cursor--interactive", isInteractive);
    innerRef.current.classList.toggle("cursor__dot--interactive", isInteractive);
  }, [isInteractive]);

  return (
    <>
      <div className="custom-cursor cursor-outer" ref={outerRef} aria-hidden="true">
        <div className="cursor__dot" ref={innerRef} />
      </div>
    </>
  );
}
