/* === Homepage Container === */

/* === Navigation === */

.home-nav {
  width: 100%;
  height: 100%;
  position: sticky;
  top: 0;
  z-index: 100;
  display: flex;
  align-items: center;
  justify-content: center;
  /* padding: 1rem 3rem; */
  border-bottom: 1px solid var(--color-border);
  backdrop-filter: blur(20px);
  -webkit-backdrop-filter: blur(20px);
  pointer-events: auto;
}

.landing-container {
  height: 100vh;
  width: 100%;
  display: grid;
  align-content: center;
  justify-content: center;
  position: relative;
  z-index: 1;
  pointer-events: none;
}

.landing-container > * {
  pointer-events: auto;
}

/* Liquid background host */
.background.liquid-bg {
  position: fixed;
  inset: 0;
  z-index: 0;
  pointer-events: none;
}

.container {
  height: 100vh;
  width: 100%;
}
