commits.content:
.flow-showcase-root {
  border-radius: 8px;
  overflow: hidden;
  background: var(--card-bg, rgba(255,255,255,0.02));
  display: block;
}

.flow-showcase .react-flow__node {
  min-width: 64px;
  padding: 6px 10px;
  border-radius: 6px;
  background: var(--node-bg, rgba(16,24,40,0.9));
  color: var(--node-fg, #fff);
  font-size: 12px;
}

.flow-showcase .react-flow__edge-path {
  stroke: rgba(255,255,255,0.6);
}

.flow-showcase .react-flow__attribution {
  display: none;
}
