+import React from "react";
+import { ReactFlow, Background } from "@xyflow/react";
+import "@xyflow/react/dist/style.css";
+import "./FlowShowcase.css";
+import { CustomNode, CustomEdge } from "../../../visualizer/components/Graph";
+import { convertToReactFlow } from "../../../visualizer/utils/graphLayout";
+
+const SIZE_WIDTH = 320;
+const SIZE_HEIGHT = 220;
+
+function FlowCanvas({ nodes, edges }) {
+  return (
+    <div
+      className="flow-showcase-root"
+      style={{ width: SIZE_WIDTH, height: SIZE_HEIGHT }}
+    >
+      <ReactFlow
+        nodes={nodes}
+        edges={edges}
+        nodeTypes={{ custom: CustomNode }}
+        edgeTypes={{ custom: CustomEdge }}
+        nodesConnectable={false}
+        nodesDraggable={false}
+        panOnDrag={false}
+        zoomOnScroll={false}
+        panOnScroll={false}
+        zoomOnPinch={false}
+        fitView
+        fitViewOptions={{ padding: 0.18 }}
+        minZoom={0.2}
+        maxZoom={1.5}
+        className="flow-showcase"
+      >
+        <Background gap={16} color="transparent" />
+      </ReactFlow>
+    </div>
+  );
+}
+
+export default function FlowShowcase({ variant = "commit" }) {
+  // Build a tiny synthetic gitGraph and use convertToReactFlow so we reuse layout logic.
+  const commits = new Map();
+  const branches = new Map();
+
+  const now = Date.now();
+
+  if (variant === "commit") {
+    // two linear commits on main: Initial commit -> Feat: add X
+    commits.set("c1", {
+      id: "c1",
+      message: "Initial commit",
+      parents: [],
+      createdByBranch: "main",
+      timestampMs: now - 2000,
+    });
+    commits.set("c2", {
+      id: "c2",
+      message: "Feat: add X",
+      parents: ["c1"],
+      createdByBranch: "main",
+      timestampMs: now - 1000,
+    });
+    branches.set("main", "c2");
+    // HEAD = "main";
+  } else if (variant === "branch") {
+    // initial commit -> branch 'frontend' -> Feat: add X (on frontend)
+    commits.set("c1", {
+      id: "c1",
+      message: "Initial commit",
+      parents: [],
+      createdByBranch: "main",
+      timestampMs: now - 2000,
+    });
+    commits.set("f1", {
+      id: "f1",
+      message: "Feat: add X",
+      parents: ["c1"],
+      createdByBranch: "frontend",
+      timestampMs: now - 1000,
+    });
+    // branch pointers: main still points to c1, frontend points to f1
+    branches.set("main", "c1");
+    branches.set("frontend", "f1");
+    // HEAD = "frontend";
+  } else if (variant === "merge") {
+    // merge example: main has an update, frontend branch off it with a feat commit, then merge into main
+    commits.set("c1", {
+      id: "c1",
+      message: "Initial commit",
+      parents: [],
+      createdByBranch: "main",
+      timestampMs: now - 3000,
+    });
+    commits.set("c2", {
+      id: "c2",
+      message: "Main update",
+      parents: ["c1"],
+      createdByBranch: "main",
+      timestampMs: now - 2000,
+    });
+    commits.set("f1", {
+      id: "f1",
+      message: "Feat: add X",
+      parents: ["c2"],
+      createdByBranch: "frontend",
+      timestampMs: now - 1200,
+    });
+    commits.set("merge", {
+      id: "merge",
+      message: "Merge 'frontend' into main",
+      parents: ["c2", "f1"],
+      createdByBranch: "main",
+      timestampMs: now - 400,
+    });
+    branches.set("main", "merge");
+    branches.set("frontend", "f1");
+    // HEAD = "main";
+  }
+
+  const orphanedCommits = new Set();
+  // Set HEAD so the visualizer can mark the active branch tip correctly.
+  // For commit and merge examples we want the main branch to be HEAD; for the branch
+  // example we want the frontend branch to be HEAD so the frontend commit shows as active.
+  let HEAD = undefined;
+  if (variant === "commit") HEAD = "main";
+  if (variant === "branch") HEAD = "frontend";
+  if (variant === "merge") HEAD = "main";
+
+  const graph = { commits, branches, orphanedCommits, HEAD };
+
+  const { nodes, edges } = convertToReactFlow(graph);
+
+  return <FlowCanvas nodes={nodes} edges={edges} />;
+}

