commits.content:
.parallax {
  position: relative;
  overflow: hidden;
}

.scroller {
  display: flex;
  white-space: nowrap;
  text-align: center;
  font-family: var(--font-display);
  font-size: 2.25rem;
  font-weight: var(--font-semibold);
  letter-spacing: -0.02em;
  line-height: 1.1;
  filter: drop-shadow(0 1px 1px rgba(0, 0, 0, 0.1));
}

.scroller span {
  flex-shrink: 0;
}

@media (min-width: 768px) {
  .scroller {
    font-size: 20rem;
    line-height: 20rem;
  }
}
