import { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import { ScrollToPlugin } from "gsap/ScrollToPlugin";
import "./Landing.css";

import Navbar from "./components/Navbar";
import Hero from "./components/Hero";
import FeaturesSection from "./components/Features";
import DemoPreview from "./components/DemoPreview";
import ArchitectureSection from "./components/Architecture";
import ContributorsSection from "./components/Contributors";
import ContactSection from "./components/Contact";
import LandingFooter from "./components/Footer";

gsap.registerPlugin(ScrollTrigger, ScrollToPlugin);

export default function Landing() {
  const navigate = useNavigate();

  useEffect(() => {
    // global ScrollTrigger defaults and refresh on route change
    ScrollTrigger.defaults({
      markers: false,
      toggleActions: "play none none reverse",
    });

    const onResize = () => ScrollTrigger.refresh();
    window.addEventListener("resize", onResize);
    return () => {
      window.removeEventListener("resize", onResize);
      // kill triggers on unmount (route away)
      ScrollTrigger.getAll().forEach((st) => st.kill());
    };
  }, []);

  const handleLaunch = () => navigate("/visualizer");

  const handleScrollTo = (id) => {
    if (id === "top") {
      gsap.to(window, { duration: 0.8, scrollTo: 0, ease: "power2.out" });
      return;
    }
    const el = document.getElementById(id);
    if (el) {
      gsap.to(window, {
        duration: 0.8,
        scrollTo: { y: el, offsetY: 72 },
        ease: "power2.out",
      });
    }
  };

  return (
    <div className="landing-root">
      <div className="landing-bg" aria-hidden="true" />

      <Navbar onNav={(target) => handleScrollTo(target)} onGoVisualizer={handleLaunch} />

      <main>
        <Hero onLaunch={handleLaunch} />
        <FeaturesSection />
        <DemoPreview />
        <ArchitectureSection />
        <ContributorsSection />
        <ContactSection />
      </main>

      <LandingFooter />
    </div>
  );
}
