/* Architecture section */
.arch-grid { display: grid; gap: 16px; grid-template-columns: repeat(2, minmax(0,1fr)); }
@media (max-width: 900px) { .arch-grid { grid-template-columns: 1fr; } }
.arch-item { padding: 20px; border-radius: 14px; }
.arch-title { font-weight: 700; color: var(--text-primary); margin-bottom: 6px; }
.arch-desc { color: var(--text-secondary); font-size: 14px; }
