/* Demo preview with pin & zoom */
.demo-section {
  padding-top: 10vh;
  padding-bottom: 10vh;
}
.demo-box {
  padding: 24px;
  border-radius: 18px;
}
.demo-head {
  font-weight: 700;
  color: var(--text-primary);
  margin-bottom: 12px;
}
.demo-body {
  aspect-ratio: 16/9;
  background: rgba(255, 255, 255, 0.04);
  border: 1px dashed rgba(255, 255, 255, 0.12);
  border-radius: 12px;
  display: grid;
  place-items: center;
  color: var(--text-secondary);
}
[data-theme="light"] .demo-body {
  background: rgba(0, 0, 0, 0.04);
  border-color: rgba(102, 126, 234, 0.24);
}
