@@ -1,2 +1,326 @@
-/* Features section custom rules if needed */
-.feature-card { will-change: transform, opacity; }
+/* Features Section - Zig-zag Layout */
+.features-section {
+  position: relative;
+  width: 100%;
+  padding: 8rem 2rem;
+  background: var(--color-bg-primary);
+  overflow: hidden;
+}
+
+.features-container {
+  max-width: 1400px;
+  margin: 0 auto;
+}
+
+/* Header */
+.features-header {
+  text-align: center;
+  margin-bottom: 6rem;
+}
+
+.features-main-title {
+  font-size: clamp(2.5rem, 5vw, 4rem);
+  font-weight: 800;
+  background: linear-gradient(
+    135deg,
+    var(--color-text-primary),
+    var(--color-primary)
+  );
+  -webkit-background-clip: text;
+  background-clip: text;
+  -webkit-text-fill-color: transparent;
+  margin-bottom: 1rem;
+  line-height: 1.2;
+}
+
+.features-main-subtitle {
+  font-size: clamp(1rem, 2vw, 1.25rem);
+  color: var(--color-text-secondary);
+  max-width: 600px;
+  margin: 0 auto;
+}
+
+/* Feature Rows */
+.features-content {
+  display: flex;
+  flex-direction: column;
+  gap: 4rem;
+  position: relative;
+}
+
+.feature-row {
+  display: grid;
+  grid-template-columns: 1fr 1fr;
+  gap: 4rem;
+  align-items: center;
+  position: relative;
+  min-height: 500px;
+}
+
+/* Left alignment: text left, demo right */
+.feature-left {
+  grid-template-areas: "text demo";
+}
+
+.feature-left .feature-text {
+  grid-area: text;
+  padding-right: 2rem;
+}
+
+.feature-left .feature-demo {
+  grid-area: demo;
+}
+
+/* Right alignment: demo left, text right */
+.feature-right {
+  grid-template-areas: "demo text";
+}
+
+.feature-right .feature-text {
+  grid-area: text;
+  padding-left: 2rem;
+}
+
+.feature-right .feature-demo {
+  grid-area: demo;
+}
+
+/* Feature Text */
+.feature-text {
+  display: flex;
+  flex-direction: column;
+  gap: 1.5rem;
+}
+
+.feature-title {
+  font-size: clamp(2rem, 4vw, 3rem);
+  font-weight: 800;
+  color: var(--color-text-primary);
+  line-height: 1.2;
+  margin: 0;
+}
+
+.feature-subtitle {
+  font-size: clamp(1.25rem, 2vw, 1.75rem);
+  font-weight: 600;
+  color: var(--color-primary);
+  line-height: 1.3;
+  margin: 0;
+}
+
+.feature-description {
+  font-size: clamp(1rem, 1.5vw, 1.125rem);
+  color: var(--color-text-secondary);
+  line-height: 1.7;
+  max-width: 540px;
+  margin: 0;
+}
+
+.feature-button {
+  align-self: flex-start;
+  padding: 0.875rem 2rem;
+  background: var(--color-primary);
+  color: white;
+  border: none;
+  border-radius: 50px;
+  font-size: 1rem;
+  font-weight: 600;
+  cursor: pointer;
+  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
+  box-shadow: 0 4px 14px rgba(236, 72, 153, 0.3);
+}
+
+.feature-button:hover {
+  transform: translateY(-2px);
+  box-shadow: 0 6px 20px rgba(236, 72, 153, 0.4);
+}
+
+.feature-button:active {
+  transform: translateY(0);
+}
+
+/* Feature Demo Window */
+.feature-demo {
+  position: relative;
+  width: 100%;
+  height: 100%;
+  display: flex;
+  align-items: center;
+  justify-content: center;
+}
+
+.feature-demo-window {
+  width: 100%;
+  height: 500px;
+  background: var(--color-bg-secondary);
+  border-radius: 16px;
+  overflow: hidden;
+  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.15), 0 8px 30px rgba(0, 0, 0, 0.1);
+  border: 1px solid var(--color-border);
+  backdrop-filter: blur(10px);
+  transition: transform 0.3s ease, box-shadow 0.3s ease;
+}
+
+.feature-demo-window:hover {
+  transform: translateY(-4px);
+  box-shadow: 0 25px 70px rgba(0, 0, 0, 0.2), 0 10px 35px rgba(0, 0, 0, 0.15);
+}
+
+.feature-demo-header {
+  height: 40px;
+  background: var(--color-bg-tertiary);
+  border-bottom: 1px solid var(--color-border);
+  display: flex;
+  align-items: center;
+  padding: 0 1rem;
+}
+
+.feature-demo-dots {
+  display: flex;
+  gap: 0.5rem;
+}
+
+.dot {
+  width: 12px;
+  height: 12px;
+  border-radius: 50%;
+  transition: opacity 0.2s;
+}
+
+.dot-red {
+  background: #ff5f56;
+}
+
+.dot-yellow {
+  background: #ffbd2e;
+}
+
+.dot-green {
+  background: #27c93f;
+}
+
+.feature-demo-window:hover .dot {
+  opacity: 0.8;
+}
+
+.feature-demo-content {
+  width: 100%;
+  height: calc(100% - 40px);
+  position: relative;
+  overflow: hidden;
+}
+
+.feature-demo-content > * {
+  height: 100%;
+}
+
+/* Connector SVG */
+.feature-connector {
+  position: absolute;
+  width: 100%;
+  height: 150px;
+  left: 0;
+  right: 0;
+  margin: -2rem 0;
+  pointer-events: none;
+  z-index: 1;
+}
+
+.connector-left-to-right {
+  transform: scaleY(1);
+}
+
+.connector-right-to-left {
+  transform: scaleY(1);
+}
+
+/* Responsive Design */
+@media (max-width: 1024px) {
+  .features-section {
+    padding: 5rem 1.5rem;
+  }
+
+  .feature-row {
+    gap: 3rem;
+    min-height: 450px;
+  }
+
+  .feature-demo-window {
+    height: 450px;
+  }
+
+  .feature-connector {
+    height: 120px;
+  }
+}
+
+@media (max-width: 768px) {
+  .features-header {
+    margin-bottom: 4rem;
+  }
+
+  .feature-row {
+    grid-template-columns: 1fr !important;
+    grid-template-areas: "text" "demo" !important;
+    gap: 2rem;
+    min-height: auto;
+  }
+
+  .feature-left .feature-text,
+  .feature-right .feature-text {
+    padding: 0;
+  }
+
+  .feature-text {
+    text-align: center;
+    align-items: center;
+  }
+
+  .feature-description {
+    max-width: 100%;
+  }
+
+  .feature-button {
+    align-self: center;
+  }
+
+  .feature-demo-window {
+    height: 400px;
+  }
+
+  .feature-connector {
+    display: none;
+  }
+
+  .features-content {
+    gap: 3rem;
+  }
+}
+
+@media (max-width: 480px) {
+  .features-section {
+    padding: 3rem 1rem;
+  }
+
+  .feature-demo-window {
+    height: 350px;
+  }
+
+  .feature-demo-header {
+    height: 32px;
+  }
+
+  .dot {
+    width: 10px;
+    height: 10px;
+  }
+}
+
+/* Animation will-change optimization */
+.feature-row {
+  will-change: transform, opacity;
+}
+
+.feature-connector path {
+  will-change: stroke-dashoffset;
+}
