/* Footer */
.landing-footer {
  border-top: 1px solid rgba(255,255,255,0.08);
  background: linear-gradient(180deg, rgba(118,75,162,0.08), transparent);
}
[data-theme="light"] .landing-footer {
  background: linear-gradient(180deg, rgba(102,126,234,0.12), rgba(255,255,255,0.8));
  border-top-color: rgba(102,126,234,0.24);
}
.footer-inner {
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px 24px;
  text-align: center;
  color: var(--text-secondary);
}
.heart { color: #ef4444; }
