/* Hero styles */
.hero-section {
  min-height: calc(100vh - 64px);
  display: grid;
  place-items: center;
  padding-top: 96px;
}

.hero-inner {
  text-align: center;
  padding: clamp(32px, 6vw, 56px);
}

.hero-title {
  font-size: clamp(48px, 9vw, 92px);
  font-weight: 900;
  letter-spacing: -1px;
  margin: 0 0 12px;
  background: linear-gradient(135deg, #cfd9ff 0%, #a98bff 100%);
  -webkit-background-clip: text;
  background-clip: text;
  color: transparent;
}

[data-theme="light"] .hero-title {
  background: linear-gradient(135deg, #4b5bd6 0%, #7b4ec7 100%);
  -webkit-background-clip: text;
  background-clip: text;
}

.hero-subtitle {
  font-size: clamp(16px, 2.4vw, 22px);
  color: var(--text-secondary);
  margin: 0 0 24px;
}

.hero-cta { display: flex; justify-content: center; }

/* Ambient orbs */
.ambient { position: absolute; inset: 0; pointer-events: none; z-index: -1; }
.orb { position: absolute; filter: blur(40px); opacity: 0.35; }
.orb-a { width: 300px; height: 300px; top: 8%; left: 12%; background: #667eea; border-radius: 50%; }
.orb-b { width: 260px; height: 260px; bottom: 12%; right: 10%; background: #764ba2; border-radius: 50%; }
.orb-c { width: 200px; height: 200px; bottom: 30%; left: 30%; background: #4f46e5; border-radius: 50%; }

[data-theme="light"] .orb { opacity: 0.25; }
