import { useEffect, useRef } from "react";
import gsap from "gsap";
import Button from "@/components/ui/Button";
import "./Hero.css";

export default function Hero({ onLaunch }) {
  const rootRef = useRef(null);
  const titleRef = useRef(null);
  const subtitleRef = useRef(null);
  const ctaRef = useRef(null);

  useEffect(() => {
    const ctx = gsap.context(() => {
      const tl = gsap.timeline();
      tl.fromTo(titleRef.current, { autoAlpha: 0, scale: 0.92 }, { autoAlpha: 1, scale: 1, duration: 0.9, ease: "power3.out" })
        .fromTo(subtitleRef.current, { y: 24, autoAlpha: 0 }, { y: 0, autoAlpha: 1, duration: 0.8, ease: "power2.out" }, "-=0.4")
        .fromTo(ctaRef.current, { y: 20, autoAlpha: 0 }, { y: 0, autoAlpha: 1, duration: 0.6, ease: "power2.out" }, "-=0.3");
    }, rootRef);
    return () => ctx.revert();
  }, []);

  return (
    <section id="home" ref={rootRef} className="section hero-section">
      <div className="section-inner hero-inner glass">
        <h1 ref={titleRef} className="hero-title">Git-Vis</h1>
        <p ref={subtitleRef} className="hero-subtitle">Demystify Git through real-time visualization</p>
        <div ref={ctaRef} className="hero-cta">
          <Button size="large" onClick={onLaunch}>Launch Visualizer ðŸš€</Button>
        </div>
      </div>
      <div className="ambient">
        <div className="orb orb-a" />
        <div className="orb orb-b" />
        <div className="orb orb-c" />
      </div>
    </section>
  );
}
