/* Navbar styles (glassmorphic, sticky) */

.landing-navbar {
  position: sticky;
  top: 0;
  z-index: 50;
  backdrop-filter: blur(12px);
  background: rgba(20,20,28,0.35);
  border-bottom: 1px solid rgba(255,255,255,0.08);
}

[data-theme="light"] .landing-navbar {
  background: rgba(255,255,255,0.75);
  border-bottom-color: rgba(102,126,234,0.24);
}

.navbar-inner {
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 14px 24px;
}

.brand {
  font-weight: 800;
  font-size: 20px;
  letter-spacing: 0.6px;
  cursor: pointer;
}

.links {
  display: none;
  gap: 16px;
}

.links button,
.links .link-route {
  background: transparent;
  border: none;
  color: var(--text-secondary);
  font-weight: 600;
  padding: 8px 10px;
  border-radius: 8px;
  cursor: pointer;
}

.links button:hover,
.links .link-route:hover {
  color: var(--text-primary);
  background: rgba(255,255,255,0.06);
}

.links button.active {
  color: var(--text-primary);
  background: rgba(255,255,255,0.09);
}

.cta {
  display: none;
}

@media (min-width: 900px) {
  .links { display: flex; }
  .cta { display: block; }
}
