import { useEffect, useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import Button from "@/components/ui/Button";
import "./Navbar.css";

export default function Navbar({ onNav, onGoVisualizer }) {
  const navRef = useRef(null);
  const navigate = useNavigate();
  const [active, setActive] = useState("home");

  useEffect(() => {
    const el = navRef.current;
    if (!el) return;

    // Opacity / glass intensifies after scroll past hero threshold
    const tl = gsap.timeline({ paused: true });
    tl.to(el, {
      backgroundColor: "rgba(20,20,28,0.55)",
      borderColor: "rgba(255,255,255,0.12)",
      boxShadow: "0 8px 30px rgba(0,0,0,0.25)",
      duration: 0.3,
      ease: "power1.out",
    });

    const st = ScrollTrigger.create({
      start: 80,
      end: 99999,
      onUpdate: (self) => {
        if (self.progress > 0) tl.play();
        else tl.reverse();
      },
    });
    return () => {
      st.kill();
      tl.kill();
    };
  }, []);

  // Update active section based on scroll position
  useEffect(() => {
    const sections = [
      { id: "home", el: document.getElementById("home") },
      { id: "features", el: document.getElementById("features") },
      { id: "demo", el: document.getElementById("demo") },
      { id: "architecture", el: document.getElementById("architecture") },
      { id: "contributors", el: document.getElementById("contributors") },
      { id: "contact", el: document.getElementById("contact") },
    ].filter((s) => s.el);

    const triggers = sections.map((s) =>
      ScrollTrigger.create({
        trigger: s.el,
        start: "top center",
        end: "bottom center",
        onToggle: (self) => self.isActive && setActive(s.id),
      })
    );

    return () => triggers.forEach((t) => t.kill());
  }, []);

  const go = (route) => navigate(route);

  return (
    <header ref={navRef} className="landing-navbar glass">
      <div className="navbar-inner">
        <div className="brand" onClick={() => onNav?.("top")}>Git-Vis</div>
        <nav className="links">
          <button className={active === "home" ? "active" : ""} onClick={() => onNav?.("top")}>Home</button>
          <button className={active === "features" ? "active" : ""} onClick={() => onNav?.("features")}>Features</button>
          <button className={active === "demo" ? "active" : ""} onClick={() => onNav?.("demo")}>Demo</button>
          <button className={active === "architecture" ? "active" : ""} onClick={() => onNav?.("architecture")}>Architecture</button>
          <button className={active === "contact" ? "active" : ""} onClick={() => onNav?.("contact")}>Contact</button>
          <button onClick={() => go("/visualizer")} className="link-route">Visualizer</button>
          <a href="https://github.com/omeepatel04/git-visualizer" target="_blank" rel="noreferrer" className="link-route">GitHub</a>
        </nav>
        <div className="cta">
          <Button size="small" onClick={onGoVisualizer}>Launch Visualizer ðŸš€</Button>
        </div>
      </div>
    </header>
  );
}
