/* Terminal Showcase Section */
.terminal-showcase-section {
  position: relative;
  width: 100%;
  padding: 8rem 2rem;
  background: linear-gradient(
    180deg,
    var(--color-bg-primary) 0%,
    var(--color-bg-secondary) 50%,
    var(--color-bg-primary) 100%
  );
  overflow: hidden;
}

.terminal-showcase-container {
  max-width: 1400px;
  margin: 0 auto;
}

.terminal-showcase-content {
  display: grid;
  grid-template-columns: 1fr 1.2fr;
  gap: 4rem;
  align-items: center;
}

/* Text Content */
.terminal-showcase-text {
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
}

.terminal-showcase-title {
  font-size: clamp(2.5rem, 5vw, 4rem);
  font-weight: 800;
  background: linear-gradient(
    135deg,
    var(--color-text-primary),
    var(--color-primary)
  );
  -webkit-background-clip: text;
  background-clip: text;
  -webkit-text-fill-color: transparent;
  line-height: 1.2;
  margin: 0;
}

.terminal-showcase-subtitle {
  font-size: clamp(1.25rem, 2vw, 1.75rem);
  font-weight: 600;
  color: var(--color-primary);
  line-height: 1.3;
  margin: 0;
}

.terminal-showcase-description {
  font-size: clamp(1rem, 1.5vw, 1.125rem);
  color: var(--color-text-secondary);
  line-height: 1.8;
  max-width: 540px;
  margin: 0;
}

/* Features List */
.terminal-showcase-features {
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
  margin-top: 1rem;
}

.showcase-feature {
  display: flex;
  gap: 1rem;
  align-items: flex-start;
}

.feature-icon {
  font-size: 2rem;
  flex-shrink: 0;
  width: 48px;
  height: 48px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: var(--color-bg-tertiary);
  border-radius: 12px;
  border: 1px solid var(--color-border);
}

.feature-text {
  flex: 1;
}

.feature-text h4 {
  font-size: 1.125rem;
  font-weight: 600;
  color: var(--color-text-primary);
  margin: 0 0 0.25rem 0;
}

.feature-text p {
  font-size: 0.95rem;
  color: var(--color-text-secondary);
  line-height: 1.6;
  margin: 0;
}

/* Demo Container */
.terminal-showcase-demo {
  position: relative;
  width: 100%;
  height: 100%;
  min-height: 600px;
}

.terminal-showcase-demo .demo-container {
  height: 100%;
  box-shadow: 0 25px 80px rgba(0, 0, 0, 0.2),
              0 12px 40px rgba(0, 0, 0, 0.15);
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.terminal-showcase-demo .demo-container:hover {
  transform: translateY(-8px);
  box-shadow: 0 30px 90px rgba(0, 0, 0, 0.25),
              0 15px 45px rgba(0, 0, 0, 0.2);
}

/* Responsive Design */
@media (max-width: 1024px) {
  .terminal-showcase-section {
    padding: 5rem 1.5rem;
  }

  .terminal-showcase-content {
    gap: 3rem;
  }

  .terminal-showcase-demo {
    min-height: 550px;
  }
}

@media (max-width: 768px) {
  .terminal-showcase-section {
    padding: 4rem 1.5rem;
  }

  .terminal-showcase-content {
    grid-template-columns: 1fr;
    gap: 3rem;
  }

  .terminal-showcase-text {
    text-align: center;
    align-items: center;
  }

  .terminal-showcase-description {
    max-width: 100%;
  }

  .terminal-showcase-features {
    align-items: center;
  }

  .showcase-feature {
    max-width: 400px;
  }

  .terminal-showcase-demo {
    min-height: 600px;
  }
}

@media (max-width: 480px) {
  .terminal-showcase-section {
    padding: 3rem 1rem;
  }

  .showcase-feature {
    flex-direction: column;
    align-items: center;
    text-align: center;
  }

  .terminal-showcase-demo {
    min-height: 550px;
  }
}

/* Animation optimizations */
.terminal-showcase-content,
.terminal-showcase-text,
.terminal-showcase-demo {
  will-change: transform, opacity;
}
