import { useEffect, useRef } from "react";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import { TerminalDemo } from "@/features/visualizer";
import "./TerminalShowcase.css";

gsap.registerPlugin(ScrollTrigger);

export default function TerminalShowcase() {
  const sectionRef = useRef(null);

  useEffect(() => {
    const ctx = gsap.context(() => {
      // Animate the section on scroll
      gsap.from(".terminal-showcase-content", {
        scrollTrigger: {
          trigger: sectionRef.current,
          start: "top 80%",
          end: "top 20%",
          toggleActions: "play none none reverse",
        },
        autoAlpha: 0,
        y: 60,
        duration: 1,
        ease: "power2.out",
      });

      gsap.from(".terminal-showcase-text", {
        scrollTrigger: {
          trigger: ".terminal-showcase-text",
          start: "top 85%",
          end: "top 30%",
          toggleActions: "play none none reverse",
        },
        autoAlpha: 0,
        x: -40,
        duration: 0.8,
        ease: "power2.out",
      });

      gsap.from(".terminal-showcase-demo", {
        scrollTrigger: {
          trigger: ".terminal-showcase-demo",
          start: "top 85%",
          end: "top 30%",
          toggleActions: "play none none reverse",
        },
        autoAlpha: 0,
        x: 40,
        duration: 0.8,
        ease: "power2.out",
      });
    }, sectionRef);

    return () => ctx.revert();
  }, []);

  return (
    <section id="terminal-showcase" ref={sectionRef} className="terminal-showcase-section">
      <div className="terminal-showcase-container">
        <div className="terminal-showcase-content">
          <div className="terminal-showcase-text">
            <h2 className="terminal-showcase-title">
              Type. Execute. Visualize.
            </h2>
            <p className="terminal-showcase-subtitle">
              Command-line interface that brings Git to life
            </p>
            <p className="terminal-showcase-description">
              Experience Git commands in a familiar terminal environment while watching 
              the commit graph update in real-time. Every command you type is instantly 
              visualized, making it easy to understand how Git operations affect your 
              repository structure.
            </p>
            <div className="terminal-showcase-features">
              <div className="showcase-feature">
                <div className="feature-icon">âœ¨</div>
                <div className="feature-text">
                  <h4>Real-time Visualization</h4>
                  <p>See every command reflected instantly on the graph</p>
                </div>
              </div>
              <div className="showcase-feature">
                <div className="feature-icon">âš¡</div>
                <div className="feature-text">
                  <h4>Simplified Commands</h4>
                  <p>Use familiar Git commands without complexity</p>
                </div>
              </div>
              <div className="showcase-feature">
                <div className="feature-icon">ðŸŽ¯</div>
                <div className="feature-text">
                  <h4>Interactive Learning</h4>
                  <p>Perfect for understanding Git fundamentals</p>
                </div>
              </div>
            </div>
          </div>

          <div className="terminal-showcase-demo">
            <TerminalDemo />
          </div>
        </div>
      </div>
    </section>
  );
}
