/* Copied from original src/components/CommitDetails.css */
.overlay {
  position: fixed;
  inset: 0;
  background: rgba(0, 0, 0, 0.65);
  backdrop-filter: blur(6px);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  padding: 20px;
  animation: fadeIn 0.2s ease-out;
}

.card {
  width: 100%;
  max-width: 560px;
  background: rgba(25, 25, 35, 0.85);
  border: 1px solid rgba(255, 255, 255, 0.08);
  border-radius: 20px;
  overflow: hidden;
  display: flex;
  flex-direction: column;
  animation: popIn 0.3s ease;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.5);
}

.card-header {
  display: flex;
  align-items: center;
  gap: 14px;
  padding: 22px;
  border-bottom: 1px solid rgba(255, 255, 255, 0.05);
  background: rgba(255, 255, 255, 0.03);
}

.icon-box {
  width: 40px;
  height: 40px;
  border-radius: 10px;
  background: linear-gradient(
    135deg,
    var(--primary-color),
    var(--secondary-color)
  );
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
}

.title {
  flex: 1;
  font-size: 18px;
  font-weight: 700;
  color: var(--text-primary);
}

.header-buttons {
  display: flex;
  align-items: center;
  gap: 10px;
}

.btn {
  display: flex;
  align-items: center;
  gap: 6px;
  padding: 8px 12px;
  font-size: 13px;
  font-weight: 600;
  border-radius: 8px;
  border: 1px solid transparent;
   
  transition: all 0.2s ease;
}

.btn.reset {
  color: #ef4444;
  border-color: rgba(239, 68, 68, 0.3);
  background: rgba(239, 68, 68, 0.1);
}
.btn.reset:hover {
  background: rgba(239, 68, 68, 0.2);
}

.btn.revert {
  color: #f59e0b;
  border-color: rgba(245, 158, 11, 0.3);
  background: rgba(245, 158, 11, 0.1);
}
.btn.revert:hover {
  background: rgba(245, 158, 11, 0.2);
}

.btn.close {
  width: 36px;
  height: 36px;
  justify-content: center;
  border: 1px solid rgba(255, 255, 255, 0.1);
  color: var(--text-secondary);
}
.btn.close:hover {
  background: rgba(255, 255, 255, 0.1);
  transform: rotate(90deg);
}

/* Disabled state */
.btn:disabled,
.branch-chip:disabled {
  opacity: 0.5;
  cursor: not-allowed;
  filter: grayscale(0.3);
}
.btn.reset:disabled,
.btn.revert:disabled {
  background: rgba(255, 255, 255, 0.06);
  border-color: rgba(255, 255, 255, 0.12);
  color: var(--text-secondary);
}
.branch-chip:disabled:hover {
  transform: none;
  background: rgba(102, 126, 234, 0.08);
}
.card-body {
  padding: 24px;
  display: flex;
  flex-direction: column;
  gap: 18px;
  overflow-y: auto;
}

.badge.read-only {
  font-size: 11px;
  font-weight: 700;
  padding: 6px 10px;
  border-radius: 999px;
  background: rgba(255, 255, 255, 0.06);
  border: 1px solid rgba(255, 255, 255, 0.12);
  color: var(--text-secondary);
}

.section {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.label {
  display: flex;
  align-items: center;
  gap: 6px;
  font-size: 11px;
  font-weight: 700;
  text-transform: uppercase;
  color: var(--text-secondary);
}

.value {
  padding: 10px 14px;
  background: rgba(255, 255, 255, 0.04);
  border: 1px solid rgba(255, 255, 255, 0.08);
  border-radius: 10px;
  color: var(--text-primary);
  font-size: 14px;
  line-height: 1.5;
}

.value.code {
  font-family: "Courier New", monospace;
  color: var(--primary-color);
  font-weight: 600;
  letter-spacing: 0.5px;
}

.parent-list {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.parent {
  display: flex;
  align-items: center;
  gap: 10px;
  padding: 10px 14px;
  border-radius: 8px;
  background: rgba(255, 255, 255, 0.03);
  transition: all 0.2s;
}
.parent:hover {
  background: rgba(255, 255, 255, 0.05);
  transform: translateX(4px);
}

.num {
  width: 22px;
  height: 22px;
  border-radius: 6px;
  background: linear-gradient(
    135deg,
    var(--primary-color),
    var(--secondary-color)
  );
  color: white;
  font-size: 11px;
  font-weight: 700;
  display: flex;
  align-items: center;
  justify-content: center;
}

.hash {
  font-family: "Courier New", monospace;
  color: var(--text-secondary);
  font-size: 12px;
}

.merge-tag {
  font-size: 13px;
  text-align: center;
  background: rgba(251, 191, 36, 0.08);
  border: 1px solid rgba(251, 191, 36, 0.3);
  border-radius: 8px;
  padding: 8px;
}

.branch-list {
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
}

.branch-chip {
  display: flex;
  align-items: center;
  gap: 6px;
  padding: 8px 12px;
  border: 1.5px solid var(--primary-color);
  border-radius: 8px;
  color: var(--primary-color);
  font-size: 12px;
  font-weight: 600;
   
  background: rgba(102, 126, 234, 0.1);
  transition: all 0.2s ease;
}
.branch-chip:hover {
  background: rgba(102, 126, 234, 0.2);
  transform: translateY(-1px);
}

/* Animations */
@keyframes popIn {
  from {
    opacity: 0;
    transform: scale(0.9) translateY(10px);
  }
  to {
    opacity: 1;
    transform: scale(1) translateY(0);
  }
}

@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

/* Responsive Design */
@media (max-width: 768px) {
  .overlay {
    padding: 10px;
  }

  .card {
    max-width: 100%;
    border-radius: 16px;
  }

  .card-header {
    padding: 16px;
    gap: 10px;
    flex-wrap: wrap;
  }

  .icon-box {
    width: 36px;
    height: 36px;
  }

  .title {
    font-size: 16px;
  }

  .header-buttons {
    gap: 8px;
    width: 100%;
    justify-content: flex-end;
  }

  .btn {
    padding: 6px 10px;
    font-size: 12px;
  }

  .btn span {
    display: none; /* Hide text on mobile, show only icons */
  }

  .btn.close {
    width: 32px;
    height: 32px;
  }

  .card-body {
    padding: 16px;
    gap: 14px;
  }

  .value {
    padding: 8px 12px;
    font-size: 13px;
  }

  .value.code {
    font-size: 11px;
    word-break: break-all;
  }

  .badge.read-only {
    font-size: 10px;
    padding: 4px 8px;
  }
}

@media (max-width: 480px) {
  .card-header {
    padding: 12px;
  }

  .title {
    font-size: 14px;
  }

  .card-body {
    padding: 12px;
  }
}
