import { useEffect, useRef } from "react";
import { ReactFlow, Background, Controls } from "@xyflow/react";
import { useGitGraph } from "../../hooks/useGitGraph";
import CustomNode from "../Graph/CustomNode";
import CustomEdge from "../Graph/CustomEdge";
import "@xyflow/react/dist/style.css";
import "./DemoShowcase.css";

const nodeTypes = {
  custom: CustomNode,
};

const edgeTypes = {
  custom: CustomEdge,
};

/**
 * CommitDemo - Shows a sequence of commits on main branch
 * Demonstrates: Basic commit flow, linear history
 */
export const CommitDemo = () => {
  const { nodes, edges, commit } = useGitGraph();
  const hasInitialized = useRef(false);

  useEffect(() => {
    if (hasInitialized.current) return;
    hasInitialized.current = true;

    // Create a sequence of commits to demonstrate linear history
    const sequence = [
      "Add README",
      "Setup project structure",
      "Implement core features",
      "Add tests",
      "Update documentation",
    ];

    let delay = 0;
    sequence.forEach((msg) => {
      setTimeout(() => {
        commit(msg);
      }, delay);
      delay += 600;
    });
  }, [commit]);

  return (
    <div className="demo-container">
      <div className="demo-header">
        <h3 className="demo-title">Commit Demo</h3>
        <p className="demo-description">Sequential commits on main branch</p>
      </div>
      <div className="demo-graph">
        <ReactFlow
          nodes={nodes}
          edges={edges}
          nodeTypes={nodeTypes}
          edgeTypes={edgeTypes}
          fitView
          fitViewOptions={{ padding: 0.2, maxZoom: 1.2 }}
          nodesDraggable={false}
          nodesConnectable={false}
          elementsSelectable={false}
          panOnDrag={false}
          zoomOnScroll={false}
          preventScrolling={false}
        >
          <Background color="#94a3b8" gap={16} />
          <Controls showInteractive={false} />
        </ReactFlow>
      </div>
    </div>
  );
};

export default CommitDemo;
