/* DemoShowcase styles */

.demo-showcase-root {
  width: 100%;
  min-height: 100vh;
  padding: 32px;
  background: var(--background-dark);
  box-sizing: border-box;
}

.demo-showcase-header {
  text-align: center;
  margin-bottom: 48px;
}

.demo-showcase-title {
  font-size: 42px;
  font-weight: 800;
  margin: 0 0 12px 0;
  background: linear-gradient(135deg, var(--primary-color), var(--accent-color));
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.demo-showcase-subtitle {
  font-size: 18px;
  color: var(--text-secondary);
  margin: 0;
}

.demo-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(500px, 1fr));
  gap: 32px;
  max-width: 1600px;
  margin: 0 auto;
}

/* Individual demo container */
.demo-container {
  background: var(--glass-background);
  border: 1px solid var(--glass-border);
  border-radius: var(--radius-lg);
  overflow: hidden;
  backdrop-filter: blur(var(--blur-md));
  -webkit-backdrop-filter: blur(var(--blur-md));
  box-shadow: 0 8px 30px var(--shadow-color);
  display: flex;
  flex-direction: column;
  height: 500px;
}

.demo-header {
  padding: 20px 24px;
  border-bottom: 1px solid var(--glass-border);
  background: var(--glass-hover);
}

.demo-title {
  font-size: 20px;
  font-weight: 700;
  margin: 0 0 6px 0;
  color: var(--text-primary);
}

.demo-description {
  font-size: 14px;
  color: var(--text-secondary);
  margin: 0;
}

.demo-graph {
  flex: 1;
  position: relative;
  overflow: hidden;
}

/* Override ReactFlow theme for demos */
.demo-graph .react-flow {
  background: transparent;
}

.demo-graph .react-flow__background {
  opacity: 0.1;
}

.demo-graph .react-flow__controls {
  background: var(--glass-background);
  border: 1px solid var(--glass-border);
  border-radius: var(--radius-sm);
}

.demo-graph .react-flow__controls-button {
  background: transparent;
  border-bottom: 1px solid var(--glass-border);
  color: var(--text-primary);
}

.demo-graph .react-flow__controls-button:hover {
  background: var(--glass-hover);
}

.demo-graph .react-flow__controls-button:last-child {
  border-bottom: none;
}

/* Responsive adjustments */
@media (max-width: 1200px) {
  .demo-grid {
    grid-template-columns: 1fr;
  }
}

@media (max-width: 768px) {
  .demo-showcase-root {
    padding: 16px;
  }

  .demo-showcase-title {
    font-size: 32px;
  }

  .demo-showcase-subtitle {
    font-size: 16px;
  }

  .demo-grid {
    gap: 20px;
  }

  .demo-container {
    height: 400px;
  }
}
