+      { action: "checkout", arg: "main" },
+      { action: "commit", arg: "Hotfix on main" },
+      { action: "merge", arg: "develop" },
+      { action: "commit", arg: "Post-merge cleanup" },
+    ];
+
+    let delay = 0;
+    sequence.forEach(({ action, arg }) => {
+      setTimeout(() => {
+        switch (action) {
+          case "commit":
+            commit(arg);
+            break;
+          case "createBranch":
+            createBranch(arg);
+            break;
+          case "checkout":
+            checkout(arg);
+            break;
+          case "merge":
+            merge(arg);
+            break;
