/* Copied from original src/components/CustomNode.css */
.custom-node-wrapper {
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 8px;
}

.node-content {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 6px;
}

.node-handle {
  width: 8px;
  height: 8px;
  background: transparent;
  border: none;
  opacity: 0;
  transition: opacity 0.2s;
}

/* Position handles at circle's vertical center and horizontal edges */
.node-handle-left {
  left: -4px;
  top: 24px; /* Half of circle height (48px/2) to center vertically */
}

.node-handle-right {
  right: -4px;
  top: 24px; /* Half of circle height (48px/2) to center vertically */
}

.custom-node-wrapper:hover .node-handle {
  opacity: 0.5;
  background: var(--glass-border);
  border: 2px solid var(--primary-color);
}

/* Main commit node */
.commit-node {
  width: 48px;
  height: 48px;
  border-radius: 50%;
  background: var(--node-bg);
  backdrop-filter: blur(20px);
  border: 3px solid var(--node-color, var(--primary-color));
  display: flex;
  align-items: center;
  justify-content: center;
   
  position: relative;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  box-shadow: 0 4px 16px var(--shadow-strong),
    0 0 20px 5px var(--node-color, var(--primary-color));
}

.commit-node:hover {
  transform: scale(1.15);
  box-shadow: 0 8px 24px var(--shadow-strong),
    0 0 30px 8px var(--node-color, var(--primary-color)),
    0 0 10px var(--node-color) inset;
}

/* HEAD node styling */
.head-node {
  border-width: 3px;
  animation: glow 2s ease-in-out infinite;
}

/* Selected node for revert operation */
.selected-node {
  border-width: 4px;
  box-shadow: 0 0 0 4px rgba(245, 158, 11, 0.3), 0 4px 16px var(--shadow-strong),
    0 0 20px 5px var(--node-color, var(--primary-color)) !important;
  animation: selectedPulse 1.5s ease-in-out infinite;
}

@keyframes selectedPulse {
  0%,
  100% {
    box-shadow: 0 0 0 4px rgba(245, 158, 11, 0.3),
      0 4px 16px var(--shadow-strong),
      0 0 20px 5px var(--node-color, var(--primary-color));
  }
  50% {
    box-shadow: 0 0 0 8px rgba(245, 158, 11, 0.5),
      0 4px 16px var(--shadow-strong),
      0 0 30px 8px var(--node-color, var(--primary-color));
  }
}

/* Orphaned node styling - greyed out and dashed */
.orphaned-node {
  border-style: dashed;
  border-width: 3px;
  opacity: 0.7; /* Increased from 0.5 for better visibility */
  background: rgba(107, 114, 128, 0.15);
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.3),
    0 0 12px 3px rgba(107, 114, 128, 0.5) !important;
  filter: grayscale(0.9);
}

.orphaned-node:hover {
  transform: scale(1.15);
  opacity: 0.85; /* Increased from 0.6 */
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.4),
    0 0 20px 5px rgba(107, 114, 128, 0.6) !important;
}

/* No glow animation for orphaned nodes */
.orphaned-node.head-node {
  animation: none;
}

.head-pulse {
  position: absolute;
  width: 100%;
  height: 100%;
  border-radius: 50%;
  border: 3px solid;
  animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
}

@keyframes pulse {
  0%,
  100% {
    transform: scale(1);
    opacity: 1;
  }
  50% {
    transform: scale(1.5);
    opacity: 0;
  }
}

@keyframes glow {
  0%,
  100% {
    box-shadow: 0 4px 16px var(--shadow-strong), 0 0 20px 5px var(--node-color);
  }
  50% {
    box-shadow: 0 4px 16px var(--shadow-strong), 0 0 40px 10px var(--node-color);
  }
}

/* Merge node icon */
.merge-node {
  background: linear-gradient(135deg, var(--node-bg), var(--glass-hover));
}

.merge-icon {
  color: var(--node-color, var(--primary-color));
  display: flex;
  align-items: center;
  justify-content: center;
  animation: rotate 3s linear infinite;
}

@keyframes rotate {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

/* Commit info display */
.commit-info {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 3px;
  padding: 6px 10px;
  background: var(--glass-background);
  backdrop-filter: blur(20px);
  border-radius: 8px;
  border: 1px solid var(--glass-border);
  min-width: 100px;
  box-shadow: 0 2px 8px var(--shadow-strong);
}

.commit-hash {
  font-family: "Courier New", monospace;
  font-size: 11px;
  font-weight: 600;
  color: var(--primary-color);
  letter-spacing: 0.5px;
}

.commit-message {
  font-size: 10px;
  color: var(--text-secondary);
  text-align: center;
  max-width: 150px;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

.head-indicator {
  font-size: 9px;
  font-weight: 700;
  color: var(--accent-color);
  background: rgba(139, 92, 246, 0.2);
  padding: 2px 8px;
  border-radius: 6px;
  border: 1px solid var(--accent-color);
  text-transform: uppercase;
  letter-spacing: 0.5px;
  animation: pulse-head 2s ease-in-out infinite;
}

.orphaned-indicator {
  font-size: 10px; /* Increased from 9px */
  font-weight: 800; /* Bolder */
  color: #ef4444; /* Red instead of grey for better visibility */
  background: rgba(239, 68, 68, 0.15);
  padding: 3px 10px; /* Increased padding */
  border-radius: 6px;
  border: 1.5px dashed #ef4444;
  text-transform: uppercase;
  letter-spacing: 0.8px;
  box-shadow: 0 0 8px rgba(239, 68, 68, 0.3);
}

.orphaned-info {
  opacity: 0.8; /* Increased from 0.6 */
}

.orphaned-info .commit-hash {
  color: #9ca3af; /* Lighter grey */
  font-weight: 700;
}

.orphaned-info .commit-message {
  color: #d1d5db; /* Much lighter */
  text-decoration: line-through;
  font-weight: 500;
}

@keyframes pulse-head {
  0%,
  100% {
    opacity: 1;
    transform: scale(1);
  }
  50% {
    opacity: 0.8;
    transform: scale(0.98);
  }
}

/* Branch badges */
.branch-badges {
  display: flex;
  flex-direction: column;
  gap: 4px;
  align-items: center;
  animation: fadeIn 0.3s ease-out;
}

.branch-badge {
  display: flex;
  align-items: center;
  gap: 6px;
  padding: 4px 10px;
  border-radius: 12px;
  border: 1.5px solid;
  font-size: 11px;
  font-weight: 600;
  backdrop-filter: blur(20px);
  white-space: nowrap;
   
  transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
  box-shadow: 0 2px 8px var(--shadow-strong);
}

.branch-badge:hover {
  transform: translateY(-2px) scale(1.05);
  box-shadow: 0 4px 12px var(--shadow-strong);
  filter: brightness(1.2);
}

/* Orphaned node branch badges - less prominent */
.orphaned-node + .branch-badges .branch-badge,
.custom-node-wrapper:has(.orphaned-node) .branch-badge {
  opacity: 0.4;
  border-color: #6b7280 !important;
  background-color: rgba(107, 114, 128, 0.1) !important;
  color: #9ca3af !important;
  text-decoration: line-through;
  cursor: not-allowed;
}

.orphaned-node + .branch-badges .branch-badge:hover,
.custom-node-wrapper:has(.orphaned-node) .branch-badge:hover {
  transform: none;
  filter: none;
}

.active-branch {
  background-color: var(--node-color, var(--primary-color)) !important;
  color: white !important;
  font-weight: 700;
  animation: shimmer 2s ease-in-out infinite;
}

@keyframes shimmer {
  0%,
  100% {
    opacity: 1;
  }
  50% {
    opacity: 0.8;
  }
}

/* Responsive adjustments */
@media (max-width: 768px) {
  .commit-node {
    width: 40px;
    height: 40px;
  }

  .branch-badge {
    font-size: 10px;
    padding: 3px 8px;
  }
}

/* Accessibility */
.commit-node:focus {
  outline: 3px solid var(--node-color, var(--primary-color));
  outline-offset: 4px;
}

.branch-badge:focus {
  outline: 2px solid var(--node-color, var(--primary-color));
  outline-offset: 2px;
}
