/* Copied from original src/components/InputModal.css */
/* === Layout === */
.modal-overlay {
  position: fixed;
  inset: 0;
  background: rgba(0, 0, 0, 0.4);
  backdrop-filter: blur(4px);
  display: flex;
  justify-content: center;
  align-items: center;
  animation: fadeIn 0.2s ease-out;
  z-index: 1000;
}

.modal-card {
  background: var(--glass-strong, rgba(30, 30, 30, 0.9));
  border: 1px solid rgba(255, 255, 255, 0.08);
  border-radius: 16px;
  width: 90%;
  max-width: 420px;
  animation: slideUp 0.25s ease-out;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.4);
}

/* === Animations === */
@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

@keyframes slideUp {
  from {
    transform: translateY(20px);
    opacity: 0;
  }
  to {
    transform: translateY(0);
    opacity: 1;
  }
}

/* === Header === */
.modal-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 18px 22px;
  border-bottom: 1px solid rgba(255, 255, 255, 0.08);
}

.modal-title {
  font-size: 18px;
  font-weight: 700;
  color: var(--text-primary, #fff);
  margin: 0;
}

.close-btn {
  background: transparent;
  border: none;
  color: var(--text-secondary, #aaa);
   
  transition: transform 0.2s, color 0.2s;
}
.close-btn:hover {
  color: var(--error-color, #ef4444);
  transform: rotate(90deg);
}

/* === Form === */
.modal-form {
  padding: 22px;
  display: flex;
  flex-direction: column;
  gap: 16px;
}

.modal-input {
  width: 100%;
  padding: 12px 14px;
  font-size: 14px;
  border-radius: 10px;
  border: 2px solid rgba(255, 255, 255, 0.1);
  background: rgba(255, 255, 255, 0.05);
  color: var(--text-primary, #fff);
  outline: none;
  transition: all 0.2s ease;
}
.modal-select {
  appearance: none;
  -webkit-appearance: none;
  -moz-appearance: none;
  background: rgba(255, 255, 255, 0.06);
  color: var(--text-primary, #fff);
   
}
.modal-select:focus {
  border-color: var(--primary-color, #6366f1);
  box-shadow: 0 0 0 3px rgba(99, 102, 241, 0.2);
}
.modal-select option {
  background-color: #1f1f2b; /* dark dropdown list */
  color: var(--text-primary, #fff);
}
/* Hide default arrow on IE */
.modal-select::-ms-expand {
  display: none;
}
.modal-input:focus {
  border-color: var(--primary-color, #6366f1);
  box-shadow: 0 0 0 3px rgba(99, 102, 241, 0.2);
}
.modal-input.has-error {
  border-color: var(--error-color, #ef4444);
  animation: shake 0.3s ease-in-out;
}

@keyframes shake {
  0%,
  100% {
    transform: translateX(0);
  }
  25% {
    transform: translateX(-5px);
  }
  75% {
    transform: translateX(5px);
  }
}

.error-text {
  font-size: 13px;
  color: var(--error-color, #ef4444);
  margin: -4px 0 8px 2px;
}

/* === Buttons === */
.modal-actions {
  display: flex;
  justify-content: flex-end;
  gap: 10px;
}

.btn {
  padding: 10px 18px;
  border: none;
  border-radius: 10px;
  font-weight: 600;
  font-size: 14px;
   
  transition: all 0.2s ease;
}

.btn.primary {
  background: var(--primary-color, #6366f1);
  color: white;
}
.btn.primary:hover {
  background: #4f46e5;
  transform: translateY(-2px);
}

.btn.secondary {
  background: rgba(255, 255, 255, 0.08);
  color: var(--text-secondary, #bbb);
}
.btn.secondary:hover {
  background: rgba(255, 255, 255, 0.15);
  color: white;
}

/* === Responsive === */
@media (max-width: 768px) {
  .modal-overlay {
    padding: 10px;
  }

  .modal-card {
    width: 95%;
    border-radius: 12px;
  }

  .modal-header {
    padding: 14px 18px;
  }

  .modal-title {
    font-size: 16px;
  }

  .modal-form {
    padding: 18px;
  }

  .modal-input {
    padding: 10px 12px;
    font-size: 13px;
  }

  .btn {
    padding: 9px 16px;
    font-size: 13px;
  }
}

@media (max-width: 600px) {
  .modal-card {
    width: 100%;
  }

  .modal-actions {
    flex-direction: column-reverse;
  }

  .btn {
    width: 100%;
  }
}

@media (max-width: 480px) {
  .modal-header {
    padding: 12px 16px;
  }

  .modal-title {
    font-size: 15px;
  }

  .modal-form {
    padding: 16px;
  }
}
