/* Copied from original src/components/Terminal.css */
/* ================================
   TERMINAL CONTAINER
================================ */
.terminal-container {
  position: fixed;
  bottom: 20px;
  left: 20px;
  right: 20px;
  height: 350px;
  background: rgba(20, 20, 25, 0.95);
  border: 1px solid rgba(255, 255, 255, 0.08);
  border-radius: 12px;
  box-shadow: 0 6px 25px rgba(0, 0, 0, 0.4);
  display: flex;
  flex-direction: column;
  font-family: "Fira Code", monospace;
  overflow: hidden;
  z-index: 1000;
  backdrop-filter: blur(20px);
}

/* ================================
   HEADER
================================ */
.terminal-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 10px 14px;
  background: rgba(40, 40, 45, 0.9);
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.terminal-title {
  color: #aaa;
  font-size: 13px;
  font-weight: 600;
  display: flex;
  align-items: center;
  gap: 6px;
}

.terminal-close {
  background: none;
  border: none;
  color: #aaa;
   
  transition: 0.2s;
  padding: 4px;
  border-radius: 4px;
}

.terminal-close:hover {
  color: #ef4444;
  background: rgba(239, 68, 68, 0.1);
}

/* ================================
   BODY
================================ */
.terminal-body {
  flex: 1;
  padding: 14px;
  overflow-y: auto;
  font-size: 13px;
  color: #d4d4d8;
  line-height: 1.6;
}

.terminal-body::-webkit-scrollbar {
  width: 6px;
}
.terminal-body::-webkit-scrollbar-thumb {
  background: rgba(161, 161, 170, 0.3);
  border-radius: 4px;
}

/* ================================
   LINES
================================ */
.terminal-line {
  display: flex;
  align-items: flex-start;
  gap: 8px;
  margin-bottom: 6px;
}

.terminal-line-system {
  color: #60a5fa;
}
.terminal-line-success {
  color: #4ade80;
}
.terminal-line-error {
  color: #f87171;
}

/* ================================
   INPUT PROMPT
================================ */
.terminal-input-line {
  display: flex;
  align-items: center;
  border-top: 1px solid rgba(255, 255, 255, 0.05);
  padding-top: 8px;
  margin-top: 8px;
  gap: 6px;
}

.terminal-input {
  flex: 1;
  background: transparent;
  border: none;
  outline: none;
  color: #d4d4d8;
  font-family: inherit;
  font-size: inherit;
}

.terminal-input::placeholder {
  color: #666;
}

/* ================================
   PROMPT SYMBOLS
================================ */
.terminal-branch {
  color: #8b5cf6;
  font-weight: 600;
}
.terminal-arrow {
  color: #4ade80;
  font-weight: 700;
}

/* ================================
   RESPONSIVE
================================ */
@media (max-width: 1024px) {
  .terminal-container {
    height: 300px;
  }

  .terminal-body {
    padding: 12px;
    font-size: 12px;
  }

  .terminal-title {
    font-size: 12px;
  }
}

@media (max-width: 768px) {
  .terminal-container {
    height: 250px;
    bottom: 10px;
    left: 10px;
    right: 10px;
    border-radius: 10px;
  }

  .terminal-header {
    padding: 8px 12px;
  }

  .terminal-title {
    font-size: 11px;
  }

  .terminal-body {
    padding: 10px;
    font-size: 11px;
  }

  .terminal-input-line {
    padding-top: 6px;
    margin-top: 6px;
  }
}

@media (max-width: 480px) {
  .terminal-container {
    height: 220px;
  }

  .terminal-body {
    padding: 8px;
    font-size: 10px;
  }
}
