/* Copied from original src/components/Toolbar.css to keep styling consistent */
/* Horizontal Pill Toolbar - Bottom Center */
.toolbar-pill {
  position: fixed;
  left: 50%;
  bottom: 20px;
  transform: translateX(-50%);
  transform-origin: center center;
  z-index: 1000;
  padding: 12px 20px;
  border-radius: 50px;
  display: flex;
  flex-direction: row;
  align-items: center;
  gap: 10px;
  max-width: calc(100vw - 40px);
  height: 64px;
  animation: slideInBottom 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.4);
  overflow-x: auto;
  overflow: hidden;
  scrollbar-gutter: stable;
  isolation: isolate;
  transition: bottom 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

/* Hide desktop toolbar on mobile */
@media (max-width: 768px) {
  .toolbar-pill {
    display: none;
  }
}

/* When terminal is open, move toolbar up */
.toolbar-pill.terminal-open {
  bottom: 380px; /* Above terminal: 350px terminal height + 20px terminal bottom + 10px gap */
}

/* Custom scrollbar */
.toolbar-pill::-webkit-scrollbar {
  height: 4px;
}

.toolbar-pill::-webkit-scrollbar-track {
  background: transparent;
}

.toolbar-pill::-webkit-scrollbar-thumb {
  background: var(--glass-border);
  border-radius: 2px;
}

@keyframes slideInBottom {
  from {
    opacity: 0;
    transform: translateX(-50%) translateY(40px);
  }
  to {
    opacity: 1;
    transform: translateX(-50%) translateY(0);
  }
}

/* Action Buttons Section */
.toolbar-actions {
  display: flex;
  flex-direction: row;
  gap: 12px;
  align-items: center;
}

/* Icon-only buttons */
.toolbar-icon-btn {
  width: 44px;
  height: 44px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: rgba(255, 255, 255, 0.03);
  border: 1.5px solid var(--glass-border);
  border-radius: 50%;
  color: var(--text-primary);
   
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  position: relative;
  overflow: visible;
}

/* Tooltip on hover */
.toolbar-icon-btn::after {
  content: attr(data-tooltip);
  position: absolute;
  left: 50%;
  bottom: calc(100% + 12px);
  transform: translateX(-50%) translateY(-10px);
  background: var(--glass-background);
  backdrop-filter: blur(20px);
  border: 1px solid var(--glass-border);
  color: var(--text-primary);
  padding: 8px 16px;
  border-radius: 12px;
  font-size: 13px;
  font-weight: 600;
  white-space: nowrap;
  opacity: 0;
  pointer-events: none;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.3);
  z-index: var(--z-tooltip);
}

.toolbar-icon-btn:hover::after,
.toolbar-icon-btn:focus-visible::after {
  opacity: 1;
  transform: translateX(-50%) translateY(0);
}

.toolbar-icon-btn:hover {
  transform: scale(1.15);
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.4);
}

.toolbar-icon-btn:active {
  transform: scale(1.05);
}

/* Individual button colors */
.btn-commit:hover {
  border-color: var(--primary-color);
  background: rgba(102, 126, 234, 0.2);
  color: var(--primary-color);
  box-shadow: 0 4px 20px rgba(102, 126, 234, 0.4);
}

.btn-branch:hover {
  border-color: var(--success-color);
  background: rgba(74, 222, 128, 0.2);
  color: var(--success-color);
  box-shadow: 0 4px 20px rgba(74, 222, 128, 0.4);
}

.btn-merge:hover {
  border-color: var(--warning-color);
  background: rgba(251, 191, 36, 0.2);
  color: var(--warning-color);
  box-shadow: 0 4px 20px rgba(251, 191, 36, 0.4);
}

.btn-reset:hover {
  border-color: var(--error-color);
  background: rgba(248, 113, 113, 0.2);
  color: var(--error-color);
  box-shadow: 0 4px 20px rgba(248, 113, 113, 0.4);
}

.btn-theme:hover {
  border-color: var(--accent-color);
  background: rgba(240, 147, 251, 0.2);
  color: var(--accent-color);
  box-shadow: 0 4px 20px rgba(240, 147, 251, 0.4);
}

.btn-theme {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.btn-theme:active {
  transform: scale(1.05) rotate(180deg);
}

.btn-rebase:hover {
  border-color: #8b5cf6;
  background: rgba(139, 92, 246, 0.2);
  color: #8b5cf6;
  box-shadow: 0 4px 20px rgba(139, 92, 246, 0.4);
}

.btn-quick:hover {
  border-color: #06b6d4; /* teal-500 */
  background: rgba(6, 182, 212, 0.2);
  color: #06b6d4;
  box-shadow: 0 4px 20px rgba(6, 182, 212, 0.4);
}

.btn-revert:hover:not(.disabled) {
  border-color: #f59e0b;
  background: rgba(245, 158, 11, 0.2);
  color: #f59e0b;
  box-shadow: 0 4px 20px rgba(245, 158, 11, 0.4);
}

.btn-revert.disabled {
  opacity: 0.4;
  cursor: not-allowed;
  pointer-events: none;
}

.btn-terminal:hover {
  border-color: #10b981; /* emerald-500 */
  background: rgba(16, 185, 129, 0.2);
  color: #10b981;
  box-shadow: 0 4px 20px rgba(16, 185, 129, 0.4);
}

/* Divider */
.toolbar-divider {
  width: 1px;
  height: 32px;
  background: linear-gradient(
    180deg,
    transparent,
    var(--glass-border),
    transparent
  );
  margin: 0 4px;
}

/* Branch Dots Section */
.branch-dots {
  display: flex;
  flex-direction: row;
  gap: 12px;
  align-items: center;
  /* overflow-x: scroll; */
}

.branch-dot {
  width: 16px;
  height: 16px;
  border-radius: 50%;
  border: none;
   
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  position: relative;
  overflow: visible;
}

/* Tooltip for branch dots */
.branch-dot::after {
  content: attr(data-tooltip);
  position: absolute;
  left: 50%;
  bottom: calc(100% + 12px);
  transform: translateX(-50%) translateY(-10px);
  background: var(--glass-background);
  backdrop-filter: blur(20px);
  border: 1px solid var(--glass-border);
  color: var(--text-primary);
  padding: 6px 12px;
  border-radius: 8px;
  font-size: 12px;
  font-weight: 600;
  font-family: "Courier New", monospace;
  white-space: nowrap;
  opacity: 0;
  pointer-events: none;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.3);
  z-index: var(--z-tooltip);
}

.branch-dot:hover::after,
.branch-dot:focus-visible::after {
  opacity: 1;
  transform: translateX(-50%) translateY(0);
}

.branch-dot:hover {
  transform: scale(1.4);
}

.branch-dot:active {
  transform: scale(1.2);
}

/* Active branch dot with pulse */
.branch-dot.active {
  width: 20px;
  height: 20px;
  box-shadow: 0 0 20px currentColor !important;
}

.dot-pulse {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  width: 100%;
  height: 100%;
  border-radius: 50%;
  border: 2px solid;
  animation: dotPulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  pointer-events: none;
  transform-origin: center center;
}

@keyframes dotPulse {
  0%,
  100% {
    transform: scale(1);
    opacity: 1;
  }
  50% {
    transform: scale(1.8);
    opacity: 0;
  }
}

/* Responsive */
@media (max-width: 1024px) {
  .toolbar-pill {
    bottom: 20px;
    padding: 10px 16px;
    gap: 12px;
  }

  .toolbar-pill.terminal-open {
    bottom: 320px; /* Above terminal: 300px terminal height + 20px */
  }

  .toolbar-icon-btn {
    width: 40px;
    height: 40px;
  }

  .branch-dot {
    width: 14px;
    height: 14px;
  }

  .branch-dot.active {
    width: 18px;
    height: 18px;
  }
}

@media (max-width: 768px) {
  .toolbar-pill {
    bottom: 10px;
    padding: 8px 12px;
    gap: 8px;
    height: 56px;
    z-index: 1000;
  }

  .toolbar-pill.terminal-open {
    bottom: 280px; /* Above terminal: 250px terminal height + 10px terminal bottom + 20px gap */
  }

  .toolbar-icon-btn {
    width: 36px;
    height: 36px;
  }

  .toolbar-icon-btn svg {
    width: 18px;
    height: 18px;
  }

  .branch-dot {
    width: 12px;
    height: 12px;
  }

  .branch-dot.active {
    width: 16px;
    height: 16px;
  }
}

/* Accessibility */
.toolbar-icon-btn:focus-visible,
.branch-dot:focus-visible {
  outline: 2px solid var(--primary-color);
  outline-offset: 3px;
}
.branch-option:focus-visible {
  outline: 3px solid var(--primary-color);
  outline-offset: 2px;
}

/* ================================
   BURGER MENU (Mobile Only)
================================ */
.burger-menu-btn {
  display: none;
  position: fixed;
  bottom: 20px;
  left: 20px;
  width: 56px;
  height: 56px;
  border-radius: 50%;
  background: var(--glass-background);
  backdrop-filter: blur(20px);
  border: 1px solid var(--glass-border);
  color: var(--text-primary);
   
  z-index: 1001;
  align-items: center;
  justify-content: center;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.3);
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.burger-menu-btn:hover {
  transform: scale(1.1);
  box-shadow: 0 6px 24px rgba(0, 0, 0, 0.4);
  border-color: var(--primary-color);
}

.burger-menu-btn:active {
  transform: scale(0.95);
}

.burger-menu-btn.terminal-open {
  bottom: 280px;
}

@media (max-width: 768px) {
  .burger-menu-btn {
    display: flex;
  }
}

/* Mobile Menu Overlay */
.mobile-menu-overlay {
  display: none;
  position: fixed;
  inset: 0;
  background: rgba(0, 0, 0, 0.7);
  backdrop-filter: blur(8px);
  z-index: 1100;
  animation: fadeIn 0.2s ease-out;
}

@media (max-width: 768px) {
  .mobile-menu-overlay {
    display: block;
  }
}

/* Mobile Menu Panel */
.mobile-menu {
  position: fixed;
  bottom: 0;
  left: 0;
  right: 0;
  max-height: 80vh;
  background: var(--glass-background);
  backdrop-filter: blur(20px);
  border: 1px solid var(--glass-border);
  border-radius: 24px 24px 0 0;
  animation: slideUpMobile 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  overflow: hidden;
  display: flex;
  flex-direction: column;
}

@keyframes slideUpMobile {
  from {
    transform: translateY(100%);
  }
  to {
    transform: translateY(0);
  }
}

/* Mobile Menu Header */
.mobile-menu-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 20px;
  border-bottom: 1px solid var(--glass-border);
  background: rgba(255, 255, 255, 0.02);
}

.mobile-menu-header h3 {
  margin: 0;
  font-size: 18px;
  font-weight: 700;
  color: var(--text-primary);
  background: linear-gradient(
    135deg,
    var(--primary-color),
    var(--accent-color)
  );
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.mobile-menu-close {
  width: 36px;
  height: 36px;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid var(--glass-border);
  color: var(--text-secondary);
   
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.2s ease;
}

.mobile-menu-close:hover {
  background: rgba(255, 255, 255, 0.1);
  color: var(--error-color);
  transform: rotate(90deg);
}

/* Mobile Menu Content */
.mobile-menu-content {
  flex: 1;
  overflow-y: auto;
  padding: 16px;
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.mobile-menu-content::-webkit-scrollbar {
  width: 4px;
}

.mobile-menu-content::-webkit-scrollbar-thumb {
  background: var(--glass-border);
  border-radius: 2px;
}

/* Mobile Menu Item */
.mobile-menu-item {
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 14px 16px;
  background: rgba(255, 255, 255, 0.03);
  border: 1px solid var(--glass-border);
  border-radius: 12px;
  color: var(--text-primary);
  font-size: 15px;
  font-weight: 500;
   
  transition: all 0.2s ease;
  width: 100%;
  text-align: left;
}

.mobile-menu-item:hover {
  background: rgba(255, 255, 255, 0.06);
  transform: translateX(4px);
  border-color: var(--primary-color);
}

.mobile-menu-item:active {
  transform: translateX(2px) scale(0.98);
}

.mobile-menu-item svg {
  flex-shrink: 0;
}

/* Mobile Menu Divider */
.mobile-menu-divider {
  height: 1px;
  background: var(--glass-border);
  margin: 12px 0;
}

/* Mobile Branches Section */
.mobile-menu-branches {
  margin-top: 8px;
}

.mobile-menu-branches h4 {
  margin: 0 0 12px 0;
  font-size: 13px;
  font-weight: 700;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  color: var(--text-secondary);
  padding: 0 4px;
}

.mobile-branch-list {
  display: flex;
  flex-direction: column;
  gap: 6px;
}

.mobile-branch-item {
  display: flex;
  align-items: center;
  gap: 10px;
  padding: 12px 16px;
  background: rgba(255, 255, 255, 0.02);
  border: 1px solid var(--glass-border);
  border-radius: 10px;
  color: var(--text-primary);
  font-size: 14px;
  font-weight: 500;
   
  transition: all 0.2s ease;
  width: 100%;
  text-align: left;
}

.mobile-branch-item:hover {
  background: rgba(255, 255, 255, 0.05);
  transform: translateX(4px);
}

.mobile-branch-item.active {
  background: rgba(102, 126, 234, 0.1);
  border-color: var(--primary-color);
}

.mobile-branch-dot {
  width: 12px;
  height: 12px;
  border-radius: 50%;
  flex-shrink: 0;
  box-shadow: 0 0 8px currentColor;
}

.mobile-branch-name {
  flex: 1;
  font-family: "Courier New", monospace;
  font-size: 13px;
}

.mobile-branch-badge {
  padding: 2px 8px;
  background: var(--primary-color);
  color: white;
  font-size: 11px;
  font-weight: 600;
  border-radius: 6px;
  text-transform: uppercase;
  letter-spacing: 0.3px;
}
